mapper.select:
select pno, pname, price, psize, pamount, pstate
from GoodsTable
where pno = #{p_no}

mapper.update:
update GoodsTable
set 
	pname = #{p_name}
	,price = #{p_price}
	,pamount = #{p_amount}
	,psize = #{p_size}
	,pstate = #{p_state}
where pno = #{p_no}

mapper.select:
select pno, pname, price, psize, pamount, pstate
from GoodsTable
where pname = #{p_name}

select pno
from GoodsTable
where pname = #{p_name}

select pname
from GoodsTable
where pno = #{p_no}

select count(*)
from GoodsTable
where pname = #{pname}
