mapper.update:
update HostTable
set leadcount = leadcount+1
where hostno = #{hostno}

mapper.select:
select tourno, tourlat, tourlng
from TourFavTable
where tourno = #{tourno}

select tourno, spottitle, spotcontent, spotlat, spotlng
from SpotTable
where tourno = #{tourno}

select tourno, userid, applydate
from ApplyTourTable
where tourno = #{tourno} and userid = #{userid}

mapper.update:
 		update TourTable
 		set cancount = cancount+1
		where tourno = #{tourno}

update UserTable
